import React from "react";
import styles from './burger-ingredient.module.css';
import { CurrencyIcon } from "@ya.praktikum/react-developer-burger-ui-components";


function BurgerIngredient(card) {
    
    let number=1;
    return(
        <div className={styles.burgerIngredient}>
           <image className={styles.image} alt={card.name} src={card.image}/>
           <div className={styles.imageprice}>
                   <div className=" mr-2 text text_type_digits-default">{card.price}</div>
                   <CurrencyIcon/>
           </div>
           <div className={`text text_type_main-default ${styles.title}`}>{card.name}</div>
           <div className={`text text_type_digits-default ${styles.nom}`}>{number}</div>
        </div>

    )
};
export default BurgerIngredient;
