function BurgerConstructor(cards) {
    
    const datan = cards.cards.cards.slice(0);


    if (datan.leghth ===0) {
        const NewBurger = {
            bun: {"_id":"643d69a5c3f7b9001cfa093c",
                "name":"Краторная булка N-200i",
                "type":"bun","proteins":80,
                "fat":24,
