                "image_large":"https://code.s3.yandex.net/react/code/bun-02-large.png","__v":0},
            ingradients: [{"_id":"643d69a5c3f7b9001cfa0941",
                        "name":"Биокотлета из марсианской Магнолии",
                        "type":"main",
                        "proteins":420,
                        "fat":142,
                        "carbohydrates":242,
                        "calories":4242,
                        "price":424,
                        "image":"https://code.s3.yandex.net/react/code/meat-01.png",
                        "image_mobile":"https://code.s3.yandex.net/react/code/meat-01-mobile.png",
                        "image_large":"https://code.s3.yandex.net/react/code/meat-01-large.png","__v":0},
                        {"_id":"643d69a5c3f7b9001cfa093e",
                        "name":"Филе Люминесцентного тетраодонтимформа",
                        "type":"main",
                        "proteins":44,
                        "fat":26,
                        "carbohydrates":85,
                        "calories":643,
                        "price":988,
                        "image":"https://code.s3.yandex.net/react/code/meat-03.png",
                        "image_mobile":"https://code.s3.yandex.net/react/code/meat-03-mobile.png",
                        "image_large":"https://code.s3.yandex.net/react/code/meat-03-large.png","__v":0}]
        }
    

    }

    else {
        const NewBurger = {
            bun: datan[0],
            ingradients: datan.slice(1,5)
        }
    

    }


     console.log (NewBurger);

    return(
        <section className={styles.burgerconstructor}>
            <ConstructorElement
              type="top"
              isLocked={true}
              text = {NewBurger.bun.name}
              price= {NewBurger.bun.price}
              thumbnail={NewBurger.bun.image_mobile}
             />
            
             
            <ConstructorElement
              type="bottom"
              isLocked={true}
              text = {NewBurger.bun.name}
              price= {NewBurger.bun.price}
              thumbnail={NewBurger.bun.image_mobile}
             />

        </section>

    )
};
export default BurgerConstructor;
